import React, { useState, useEffect } from 'react';
import api from './services/api';

import './global.css';
import './App.css';
import './Sidebar.css';
import './Main.css';

function App() {
    const [devs, setDevs] = useState([]);

    const [github_username, setGithubUsername] = useState('');
    const [techs, setTechs] = useState('');
    const [latitude, setLatitude] = useState('');
    const [longitude, setLongitude] = useState('');

    useEffect(() => {
        navigator.geolocation.getCurrentPosition(
            (position) => {
                const { latitude, longitude } = position.coords;

                setLatitude(latitude);
                setLongitude(longitude);
            },
            (err) => {
                console.log(err);
            },
            {
                timeout: 30000,
            }
        )
    }, []);

    useState


    async function handleAddDev(e) {
        e.preventDefault();

        const response = await api.post('/devs',{
            github_username,
            techs,
            latitude,
            longitude,
        })

        setGithubUsername('');
        setTechs('');
    }

  return(
    <div id="app">
        <aside>
            <strong>Cadastrar</strong>
            <form onSubmit={handleAddDev}>
                <div className="input-block">
                    <label htmlFor="github_username">Usuário do Github</label>
                    <input 
                        name="github_username" 
                        id="github_username" 
                        required 
                        value={github_username}
                        onChange={e => setGithubUsername(e.target.value)}
                    />
                </div>

                <div className="input-block">
                    <label htmlFor="techs">Tecnologias</label>
                    <input 
                        name="techs" 
                        id="techs" 
                        required 
                        value={techs}
                        onChange={e => setTechs(e.target.value)}
                        />
                </div>

                <div className="input-group">
                    <div className="input-block">
                        <label htmlFor="latitude">Latitude</label>
                        <input 
                            type="number" 
                            name="latitude"
                            id="latitude" 
                            required 
                            value={latitude}
                            onChange={e => setLatitude(e.target.value)} 
                        />
                    </div>

                    <div className="input-block">
                        <label htmlFor="longitude">Longitude</label>
                        <input 
                            type="number" 
                            name="longitude" 
                            id="longitude" 
                            required 
                            value={longitude} 
                            onChange={e => setLongitude(e.target.value)}
                        />
                    </div>
                </div>

                <button type="submit">Salvar</button>
            </form>
        </aside>
        <main>
            <ul>
                <li className="dev-item">
                    <header>
                        <img src="https://avatars2.githubusercontent.com/u/2254731?s=460&u=4fcc8ca9672eeb41ea800271831b7c687bc17054&v=4"></img>
                        <div className="user-info">
                            <strong>Diego Fernandes</strong>
                            <span>ReactJS, React Native, Node.JS</span>
                        </div>
                    </header>
                    <p>CTO at @Rocketseat. Passionate about education and changing people's lives through programming...</p>
                    <a href="https://github.com/diego3g">Acessar perfil no Github</a>
                </li>
                <li className="dev-item">
                    <header>
                        <img src="https://avatars2.githubusercontent.com/u/2254731?s=460&u=4fcc8ca9672eeb41ea800271831b7c687bc17054&v=4"></img>
                        <div className="user-info">
                            <strong>Diego Fernandes</strong>
                            <span>ReactJS, React Native, Node.JS</span>
                        </div>
                    </header>
                    <p>CTO at @Rocketseat. Passionate about education and changing people's lives through programming...</p>
                    <a href="https://github.com/diego3g">Acessar perfil no Github</a>
                </li>
                <li className="dev-item">
                    <header>
                        <img src="https://avatars2.githubusercontent.com/u/2254731?s=460&u=4fcc8ca9672eeb41ea800271831b7c687bc17054&v=4"></img>
                        <div className="user-info">
                            <strong>Diego Fernandes</strong>
                            <span>ReactJS, React Native, Node.JS</span>
                        </div>
                    </header>
                    <p>CTO at @Rocketseat. Passionate about education and changing people's lives through programming...</p>
                    <a href="https://github.com/diego3g">Acessar perfil no Github</a>
                </li>
                <li className="dev-item">
                    <header>
                        <img src="https://avatars2.githubusercontent.com/u/2254731?s=460&u=4fcc8ca9672eeb41ea800271831b7c687bc17054&v=4"></img>
                        <div className="user-info">
                            <strong>Diego Fernandes</strong>
                            <span>ReactJS, React Native, Node.JS</span>
                        </div>
                    </header>
                    <p>CTO at @Rocketseat. Passionate about education and changing people's lives through programming...</p>
                    <a href="https://github.com/diego3g">Acessar perfil no Github</a>
                </li>
            </ul>
        </main>
    </div>
  );
}

export default App;
